mapper.insert:
INSERT INTO t_novel_pick_mgt
(
  main_novel_seqno,   -- 메인 소설 순번
  user_id             -- 찜한 사용자 아이디
)
VALUES 
(
  #{main_novel_seqno},
  #{user_id}
)

mapper.delete:
DELETE FROM
  t_novel_pick_mgt
WHERE
  main_novel_seqno = #{main_novel_seqno}
  AND user_id = #{user_id}

mapper.insert:
INSERT INTO t_novel_like_mgt
(
  sub_novel_seqno,    -- 서브 소설 순번
  user_id             -- 찜한 사용자 아이디
)
VALUES 
(
  #{sub_novel_seqno},
  #{user_id}
)

mapper.delete:
DELETE FROM
  t_novel_like_mgt
WHERE
  sub_novel_seqno = #{sub_novel_seqno}
  AND user_id = #{user_id}
