mapper.select:
SELECT tsnm.sub_title, tnm.content || ' ' || tsnm.sub_content AS content, -- 메인, 서브 소설 테이블에 기록된 content를 합친 데이터 tsnm.sub_like_count, tsnm.main_novel_seqno, tsnm.sub_novel_seqno, tsnm.genre_1, tsnm.genre_2, tsnm.keyword_1, tsnm.keyword_2, tsnm.keyword_3, tsnm.sub_description, tsnm.created_user AS sub_author_id, -- 서브 소설 작성자 ID tnm.created_user AS main_author_id -- 메인 소설 작성자 ID FROM t_sub_novel_mgt tsnm LEFT JOIN t_novel_mgt tnm ON tsnm.main_novel_seqno = tnm.novel_seqno WHERE tsnm.sub_like_count = ( SELECT MAX(tsnm2.sub_like_count) FROM t_sub_novel_mgt tsnm2 WHERE
tsnm2.main_novel_seqno = #{seq}
)
